/*https://projecteuler.net/problem=8*/

#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int charToInt(char c)
{
    int n;
    switch (c)
    {
        case '1':
            n = 1;
            break;
        case '2':
            n = 2;
            break;
        case '3':
            n = 3;
            break;
        case '4':
            n = 4;
            break;
        case '5':
            n = 5;
            break;
        case '6':
            n = 6;
            break;
        case '7':
            n = 7;
            break;
        case '8':
            n = 8;
            break;
        case '9':
            n = 9;
            break;
        case '0':
            n = 0;
            break;
    }
    return n;
}

int main(int argc, char** argv)
{
    char* input = "";
    
    long int max = 0;
    long int mult = 0;
    long int v = 0, x = 0;

    for (int i = 0; i < 1000; i++)
    {
        v = charToInt(input[i]);
        mult = v;
        printf("%ld ",v);
        for (int j = i+1; j < i + 13; j++)
        {
            x = charToInt(input[j]);
            if (x == 0) break;
            printf("%ld ",x);
            mult *= x;
        }
        printf("=%ld\n",mult);
        if (mult > max) max = mult;
        mult = 0;
    }

    printf("%ld\n", max);

    return 0;
}
